sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
	var countOverDue = new GlideAggregate('x_58872_needit_needit_task');
	var currentDateTime = new GlideDateTime();
	countOverDue.addQuery('due_date','<=', currentDateTime);
	countOverDue.addQuery('state','<',3);
	countOverDue.addQuery('assigned_to', current.assigned_to);
	countOverDue.addAggregate('COUNT');
	countOverDue.query();
	var totalOverDueCount = 0;
	while(countOverDue.next()) {
		totalOverDueCount = countOverDue.getAggregate('COUNT');
	}
	//template.print("In addition to this overdue NeedIt Task, you have " + (overdueNITasks.getRowCount() - 1) + " more overdue NeedIt Tasks.");
// 	(totalOverDueCount - 1)
	template.print('You have an additional ' + totalOverDueCount + 'more to complete.');

})(current, template, email, email_action, event);
